import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';import '/core/app_export.dart';import '../models/debitcardlist_item_model.dart';import '../models/paymentmethod_item_model.dart';import 'package:hari_s_application3/presentation/select_payment_method_screen/models/select_payment_method_model.dart';part 'select_payment_method_event.dart';part 'select_payment_method_state.dart';/// A bloc that manages the state of a SelectPaymentMethod according to the event that is dispatched to it.
class SelectPaymentMethodBloc extends Bloc<SelectPaymentMethodEvent, SelectPaymentMethodState> {SelectPaymentMethodBloc(SelectPaymentMethodState initialState) : super(initialState) { on<SelectPaymentMethodInitialEvent>(_onInitialize); }

_onInitialize(SelectPaymentMethodInitialEvent event, Emitter<SelectPaymentMethodState> emit, ) async  { emit(state.copyWith(selectPaymentMethodModelObj: state.selectPaymentMethodModelObj?.copyWith(debitcardlistItemList: fillDebitcardlistItemList(), paymentmethodItemList: fillPaymentmethodItemList()))); } 
List<DebitcardlistItemModel> fillDebitcardlistItemList() { return [DebitcardlistItemModel(iconImage: ImageConstant.imgTelevisionPrimary, debitCardText: "Debit Card", investText: "Invest small amounts", contrastImage: ImageConstant.imgContrastPink500), DebitcardlistItemModel(iconImage: ImageConstant.imgIcon, debitCardText: "Bank Transfer", investText: "Invest big amounts", contrastImage: ImageConstant.imgContrastIndigoA10001)]; } 
List<PaymentmethodItemModel> fillPaymentmethodItemList() { return [PaymentmethodItemModel(iconImage: ImageConstant.imgCibApplePay, titleText: "Apple Pay", descriptionText: "Connect your Apple Pay"), PaymentmethodItemModel(iconImage: ImageConstant.imgCompanyLogoPaypal, titleText: "PayPal", descriptionText: "Connect your PayPal account")]; } 
 }
