_onInitialize(SelectStocksInitialEvent event, Emitter<SelectStocksState> emit, ) async  { emit(state.copyWith(searchController: TextEditingController())); emit(state.copyWith(selectStocksModelObj: state.selectStocksModelObj?.copyWith(stocklistItemList: fillStocklistItemList()))); } 
 }
